mapper.update:
update report
SET report_reply= #{reportReply}
WHERE report_id = #{reportId}

update report
set reportTitle= #{reportTitle}
where report_id = #{reportId}

update report
set report_view= report_view + 1
where report_id = #{reportId}

mapper.select:
select count(*)
from report
where report_category = #{category1}
